/**
 * Represents the possible fillMode options of the dropdowns.
 *
 * The possible values are:
 * - `'solid'`
 * - `'flat'`
 * - `'outline'`
 * - `null`
 */
export declare type DropDownFillMode = 'solid' | 'flat' | 'outline';
