/**
 * Represents the possible rounded options of the dropdowns.
 *
 * The possible values are:
 * - `'small'`
 * - `'medium'`
 * - `'large'`
 * - `'full'`
 * - `null`
 */
export declare type DropDownRounded = 'small' | 'medium' | 'large' | 'full';
